def bad_proposal_pdf(mu, sigma):
    return (
        sts.norm.pdf(mu, loc=-5, scale=1.5) * 
        sts.norm.pdf(sigma, loc=2, scale=0.5))


def good_proposal_pdf(mu, sigma):
